function quickSort(lista) {
    if (lista.length <= 1) return lista;
    const pivo = lista[Math.floor(lista.length / 2)];
    const esquerda = lista.filter(x => x < pivo);
    const meio = lista.filter(x => x === pivo);
    const direita = lista.filter(x => x > pivo);
    return [...quickSort(esquerda), ...meio, ...quickSort(direita)];
}

